document.addEventListener("DOMContentLoaded",(function(){new Vue({el:"#enigma",data:{time:null,battery:.66},computed:{timeHour(){return this.time?this.time.format("h.mm"):""},timeHemi(){return this.time?this.time.format("A"):""},timeDay(){return this.time?this.time.format("D"):""},timeWeek(){return this.time?this.time.format("ddd"):""},batteryPercentage(){return String(100*this.battery)+"%"}},template:'<article id="enigma">\n\n\t\t\t<div class="bottom-bar">\n\n\t\t\t\t<div class="bottom-bar-left">\n\n\t\t\t\t\t<section class="time widget">\n\n\t\t\t\t\t\t<div class="time-short">\n\t\t\t\t\t\t\t<span class="time-hour">{{timeHour}}</span>\n\t\t\t\t\t\t\t<span class="time-hemi">{{timeHemi}}</span>\n\t\t\t\t\t\t</div>\n\n\t\t\t\t\t\t<div class="time-long">\n\t\t\t\t\t\t\t<span class="time-day">{{timeDay}}</span>\n\t\t\t\t\t\t\t<span class="time-week">{{timeWeek}}</span>\n\t\t\t\t\t\t</div>\n\n\t\t\t\t\t</section>\n\n\t\t\t\t</div>\n\n\t\t\t\t<div class="bottom-bar-right">\n\n\t\t\t\t\t<section class="widget"><i class="fas fa-fw fa-cloud-moon"></i></section>\n\t\t\t\t\t<section class="widget"><i class="fas fa-fw fa-cog"></i></section>\n\t\t\t\t\t<section class="widget"><i class="fas fa-fw fa-battery-full fa-rotate-270"></i></section>\n\n\t\t\t\t</div>\n\n\t\t\t</div>\n\t\t\t\n\t\t</article>',mounted(){this.updateTime(),this.updateBattery(),this.startTimer()},methods:{startTimer(){setTimeout((()=>{this.updateTime(),this.startTimer()}),1e3)},updateTime(){this.time=dayjs()},updateBattery(){navigator.getBattery().then((t=>{this.battery=t.level}))}}})}));
